mapper.select:
select employee_id as id, first_name as firstName, last_name as lastName, phone_number as phoneNumber, email, hire_date as hireDate, job_id as jobId, salary, commission_pct as commissionPct, manager_id as managerId, department_id as departmentId from employees
first_name = #{firstName}
and job_id = #{jobId}
and to_char(hire_date, 'yyyy') = to_char(hireDate, 'yyyy')
and department_id = #{departmentId}
order by employee_id asc

select employee_id as id, first_name as firstName, last_name as lastName, phone_number as phoneNumber, email, hire_date as hireDate, job_id as jobId, salary, commission_pct as commissionPct, manager_id as managerId, department_id as departmentId from employees
first_name = #{keyword}
job_id = #{keyword}
department_id = #{keyword}

select employee_id as id, first_name as firstName, last_name as lastName, phone_number as phoneNumber, email, hire_date as hireDate, job_id as jobId, salary, commission_pct as commissionPct, manager_id as managerId, department_id as departmentId from employees
and job_id = #{job}
and department_id = #{dept}

select *
from
employees


		and employee_id = #{id}


		and job_id = #{job}


		and department_id = #{dept}

select employee_id as id, first_name as firstName, last_name as lastName, phone_number as phoneNumber, email, hire_date as hireDate, job_id as jobId, salary, commission_pct as commissionPct, manager_id as managerId, department_id as departmentId from employees
first_name = #{keyword}
and department_id in
#{deptno}

mapper.update:
update employees
first_name = #{firstName}, last_name = #{lastName}, email = #{email}, phone_number = #{phoneNumber}, hire_date = #{hireDate}, job_id = #{jobId}, salary = #{salary}, commission_pct = #{commissionPct}, manager_id = #{managerId}, department_id = #{departmentId},
where employee_id = #{id}
